mapper.select:
SELECT NOTICE_NO, TITLE, CONTENTS, CREATED_AT, MODIFIED_AT, HIT
  FROM NOTICE_T 
 WHERE NOTICE_NO= #{noticeNo}

mapper.insert:

  SELECT NOTICE_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO NOTICE_T(
    NOTICE_NO
  , TITLE
  , CONTENTS
  , CREATED_AT
  , MODIFIED_AT
) VALUES(
    #{noticeNo}
  , #{title}
  , #{contents}
  , TO_DATE(SYSDATE, 'YYYY-MM-DD HH:MI:SS')
  , TO_DATE(SYSDATE, 'YYYY-MM-DD HH:MI:SS')
)

mapper.update:
UPDATE NOTICE_T
   SET TITLE = #{title}
     , CONTENTS = #{contents}
     , MODIFIED_AT = TO_DATE(SYSDATE, 'YYYY-MM-DD HH:MI:SS')
 WHERE NOTICE_NO = #{noticeNo}
