mapper.insert:
INSERT INTO IMAGE_T (
    IMAGE_PATH
  , FILESYSTEM_NAME
  , THUMBNAIL
  , PRODUCT_NO
) VALUES (
    #{imagePath}
  , #{filesystemName}
  , #{thumbnail}
  , #{productNo}
)

mapper.select:
SELECT PRODUCT_NO, USER_NO, MOUNTAIN_NO, TRIP_NAME, TRIP_CONTENTS, GUIDE, TIMETAKEN,PRIZE, DANGER, REGISTERED_AT, MODIFIED_DATE, PEOPLE,  THUMBNAIL, PLAN, TERM_USE
  FROM PRODUCT_T
 WHERE PRODUCT_NO = #{productNo}
